import React, { useState } from "react";
import { Button, Modal, Form } from "react-bootstrap";
import app from "../base";
import { DashboardLayout } from "../components/Layout";

const database = app.database();
const Children = () => {
  const [childFormData, setChildFormData] = useState({
    name: "",
    uid: "",
    skey: "",
  });
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleChange = (type) => {
    setChildFormData({ ...childFormData, [type]: event.target.value });
  };

  const handleSubmit = () => {
    console.log(childFormData);
  };
  const AddChildModal = (second) => {
    return (
      <>
        <Modal
          show={show}
          onHide={handleClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Add Child</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3 " controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="text"
                  value={childFormData.name}
                  onChange={(e)=>handleChange("name")}
                  placeholder="Child Name"
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Unique ID</Form.Label>
                <Form.Control
                  type="text"
                  value={childFormData.uid}
                  onChange={(e)=>handleChange("uid")}
                  placeholder="Unique ID"
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Secret Key</Form.Label>
                <Form.Control
                  type="password"
                  value={childFormData.skey}
                  onChange={(e)=>handleChange("skey")}
                  placeholder="Secret Key"
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Cancel
            </Button>
            <Button variant="primary" onClick={handleSubmit}>Add</Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  };
  return (
    <DashboardLayout>
      {AddChildModal()}
      <div className=" p-10 d-flex justify-content-end">
        <Button onClick={handleShow}>Add Child </Button>
      </div>
    </DashboardLayout>
  );
};

export default Children;
